current.work_notes="DisplayName: "+ displayName;
var catalysturl;
var catalystusername;
var catalystpass;
var rlcat=new GlideRecord('x_14768_user_servi_configure_application');
rlcat.addQuery('class_name','RLCatalyst');
rlcat.query();
if(rlcat.next())
{
	catalystusername=rlcat.username.toString();
	var tempass = rlcat.password;
	catalystpass = tempass.getDecryptedValue(); 
	catalysturl=rlcat.url.toString();
}


var remedy_home=catalysturl;
var remedy_auth=remedy_home+"/auth/signin";
var remedy_link=remedy_home+"/bot/adding_new_jirauser_bot/execute";
var jirausername;
var jirapassword;
var jiraurl;
var apptable=new GlideRecord('x_14768_user_servi_configure_application');
apptable.addQuery('class_name','Jira');
apptable.query();
if(apptable.next())
{
	jirausername=apptable.username.toString();
	var temppass = apptable.password;
	jirapassword = temppass.getDecryptedValue(); 
	jiraurl=apptable.url.toString();
}

var remedy_body={
	"type": "script",
		"data":{
			"username": username,
			"password": password,
			 "emailAddress": emailAddress,
			  "displayName": displayName,
			"jiraUser": jirausername,
			"jiraPass" : jirapassword,
			"url":jiraurl 
		}
};
var remedy_cred={
	"username":catalystusername,
	"pass":catalystpass,
	"authType":"token"
};


try{
	var catalyst_token = catalyst_login(remedy_auth, remedy_cred) ;
	current.work_notes=catalyst_token;
	if(catalyst_token){
		var resp=call_bot(remedy_link,remedy_body,catalyst_token);
		workflow.scratchpad.temp=resp.toString();
		if(resp=='200' || resp =="201")
		{
			current.work_notes="Add users to jira Successful";

		}else{
			current.work_notes="Add users to jira Failed";
		}
	}
	else{
		current.work_notes="Api return token failed";
	}
}
catch(err){
	current.work_notes="error: "+err;
	}

current.work_notes="End: AutoRemedy";
gs.info("End: AutoRemedy");


function catalyst_login(remedy_auth, remedy_cred){
	var msg = new sn_ws.RESTMessageV2();
	msg.setEndpoint(remedy_auth);
	msg.setHttpMethod("POST");
	msg.setRequestHeader("Content-Type", "application/json");
	msg.setRequestHeader("Accept", "application/json");
	msg.setRequestBody(JSON.stringify(remedy_cred));
	var res = msg.execute();
	gs.info("Login::Responds: "+ res.getBody());
	gs.info("!!!!!!!!!!!"+JSON.parse(res.getBody()).token);
	return JSON.parse(res.getBody()).token;

}


function call_bot(remedy_link, remedy_body, catalyst_token){	
	gs.info('Execution called'+JSON.stringify(remedy_body)+catalyst_token);
	var msg = new sn_ws.RESTMessageV2();
	msg.setEndpoint(remedy_link);
	msg.setHttpMethod("POST");
	msg.setRequestHeader("Content-Type", "application/json");
	msg.setRequestHeader("x-catalyst-auth", catalyst_token);
	msg.setRequestHeader("Accept", "application/json");
	msg.setRequestBody(JSON.stringify(remedy_body));
	gs.info("Remedy::Request:" + remedy_link);
	gs.info("Remedy::Content:" + JSON.stringify(remedy_body));
	var res = msg.execute();
	gs.info("Remedy::Responds: "+ res.getBody());
	var httpStatus = res.getStatusCode();
	gs.info('Execution get Method status' +httpStatus);
	return httpStatus;
	
}

